import React, { Component } from 'react'
import ReactStars from 'react-rating-stars-component'
import './product-model.css'
class Product_Model extends Component {
    constructor(props) {
        super(props)
        this.state = {
            classed: true
        }
        this.toggleStyle = this.toggleStyle.bind(this)
        this.toggleVertlcal = this.toggleVertlcal.bind(this)
        this.toggleHorizontal = this.toggleHorizontal.bind(this)
    }
    toggleStyle() {
        this.setState(prev => {
            return { classed: !prev.classed }
        })
    }
    toggleVertlcal() {
        this.setState({
            classed: true
        })
    }
    toggleHorizontal() {
        this.setState({
            classed: false
        })
    }
    render() {

        return (
            <div>
                <div className="mt-2">
                    <div className={`white product-model  mb-0 row simag border border-bottom-0  py-2 `}>
                        <div >
                            <span className="TSO h6"> Deals of the day</span>
                        </div>
                        <div className="ml-auto hideam float-right ">
                            <span className="font-weight-bold">Sort by :</span>
                            <span className="popular ml-1">  Popolarized <span className='fa fa-angle-down px-1' /> </span>
                        </div>
                    </div>
                    <div className={`white  product-model mb-0  row simag border py-2`}>
                        <div className="TPO">
                            1264 products found
                        </div>
                        <div className="ml-auto float-right ">
                            {/* <span onClick={this.toggleStyle}>toggle</span> */}
                            <span className={`fa fa-th-large mx-2  ${this.state.classed ? 'PM-color' : 'MP-color'} w-100}`} onClick={this.toggleVertlcal}></span>
                            <span className={`fa fa-th-list mx-2 mr-3  ${!this.state.classed ? 'PM-color' : 'MP-color'} w-100}`} onClick={this.toggleVertlcal} onClick={this.toggleHorizontal}></span>
                        </div>
                    </div>
                </div>

                <div className={`mt-0 row simpl simag border product-mode mt-1 mt-md-2`}>
                    <div className={`pack pb-1 px-1  m-0 p-0 ${this.state.classed ? 'PM-vert' : 'PM-horin'}`}>
                        <div className="row p-0 m-0 pack-row">
                            <div className="PM-pix py-4 border border-danger "></div>
                            <div className="PM-content  border border-primary px-1 ">
                                <div className="flxam">
                                    <div className="PM-title gaw">Sanforst 6.8kg Twin Semi Automatic Washing Machine for you Production</div>
                                    <div className="PM-price">â‚¦ 7,990</div>
                                    <div className="PM-PriceChange">
                                        <span className="P_DICOUNT font-weight-light"> N 321323</span>
                                        <span className="DICOUNT ml-2">10%</span>
                                    </div>

                                    <div className="float-left">  <ReactStars
                                        isHalf={true}
                                        count={5}
                                        color={'#c2c1c1'}
                                        activeColor={'#ffa601af'}
                                        size={19.1}
                                        value={3}
                                        edit={false}
                                    /></div><div className="float-left mt-2 s rated">(490)</div>
                                    <div className="PM-cart py-2 px-1 mt-4 float-right">
                                        <button className="btn btn-block z-depth-1 rounded btn-primary py-2">ADD TO CART</button>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}
export default Product_Model
