import React, { Component } from "react";
import { BrowserRouter as Router, Route, Switch } from "react-router-dom";
import Nav from './layout/nav'
import Sidenav from './layout/nav/SideNav'
import { Footer } from './layout/Footer'
import Basic_Details from './adimn/Products/New_Products/Basic_Details'
import Product_Pix from './adimn/Products/New_Products/Product_Pix'
import Selector from './adimn/Products/New_Products/Description'

import "./asset/styles/index.scss"
import { ADD_PIX } from "./store/A_store/types/Form_types";

class App extends Component {
  constructor() {
    super()
    this.state = {
      close: null
    }
    this.close = this.close.bind(this)
  }
  close() {
    this.setState(prev => {
      return { close: !prev.close }
    })

  }
  render() {
    return (
      <Router>
        <div className="app">
          <Sidenav class={this.close} side={this.state.close} />
          <Nav side={this.close} class={this.state.close} />
          <Switch>
            <Route path="/add_desc" component={Selector} />
            <Route path="/add_image" component={Product_Pix} />
            <Route path="/" component={Basic_Details} />
          </Switch>
          <Footer />
        </div>
      </Router>
    )
  }
}

export default App;
