import React, { Component } from 'react'
import renderHTML from 'react-render-html'
import { withRouter } from 'react-router-dom'
import Swiper from 'react-id-swiper'
class ProductCarousel extends Component {

    render() {
        // function dd() {
        //     return (
        //         <div>sd</div>
        //     )
        // }
        const params = {

            scrollbar: {
                el: '.swiper-scrollbar',
                hide: false
            }
            ,
            slidesPerView: 2,

            slidesPerView: 'auto',
            spaceBetween: 2,
        }

        return (
            <>

                <div id="carouselExampleFade" class=" carousel h-100 slide " data-ride="carousel">
                    <Swiper {...params}
                        class="carousel-inner h-100  rounded-lg">
                        <div class="carou black tene active px-3">
                            <img class="d-block cas  w-100 h-100 " src="https://mdbootstrap.com/img/Photos/Slides/img%20(15).jpg" alt="First slide" />
                        </div>
                        <div class="  tene h-100">
                            <img class="d-block cas w-100 h-100 " src="https://mdbootstrap.com/img/Photos/Slides/img%20(16).jpg" alt="Second slide" />
                        </div>
                        <div class="carousel-item  tene h-100">
                            <img class="d-block cas w-100 h-100 " src="https://mdbootstrap.com/img/Photos/Slides/img%20(17).jpg" alt="Third slide" />
                        </div>
                        <div class="carousel-item  tene h-100">
                            <img class="d-block cas w-100 h-100 " src="https://mdbootstrap.com/img/Photos/Slides/img%20(17).jpg" alt="Third slide" />
                        </div>
                        <div class="carousel-item  tene h-100">
                            <img class="d-block cas w-100 h-100 " src="https://mdbootstrap.com/img/Photos/Slides/img%20(17).jpg" alt="Third slide" />
                        </div>
                        <div class="carousel-item  tene h-100">
                            <img class="d-block cas w-100 h-100  mr-1" src="https://mdbootstrap.com/img/Photos/Slides/img%20(17).jpg" alt="Third slide" />
                        </div>
                    </Swiper>

                </div>


            </>
        )
    }
}

export default withRouter(ProductCarousel)
